import { SettingsOutlined } from '@material-ui/icons';
import React from 'react';
import { useState } from 'react';
import firebase from './firebase.js';


 const Contact = () => {
    const [data ,setData] =useState({
        fullname:'',
        phone:'',
        email:'',
        msg:'',
    });
 
    const InputEvent =(event) =>{
        const {name, value} = event.target;

        setData((preVal) =>{
            return {
                ...preVal,
                [name]:value,
            };
        });
    };
    // const formSubmit = (e) =>{
    //     e.preventDefault();
    //     alert(
    //         ` Thanku Your Form Submitted Successfully. 
    //           Yours details are : 
    //           Name -> ${data.fullname}. 
    //           Mobile Number -> ${data.phone}. 
    //           Email -> ${data.email}. 
    //           Your Message -> ${data.msg}`
    //     );

    // };

    const createToDo = () =>{
      const todoRef =firebase.database().ref('Users');
      const todo = {
        Name : data.fullname,
        Phone : data.phone,
        Email :data.email,
        Message : data.msg
      };
      todoRef.push(todo);
    };

    return (
    <>
    <div className="my-5">
        <h1 className="text-center">Contact Us</h1>
    </div> 
    <div className="container contact-div">
        <div className="row">
            <div className="col-md-6 col-10 mx-auto">
                <form>

                
  <div className=" mb-3 ">
    <label for="exampleFormControlInput1">Name</label>
    <input type="text" 
    className="form-control" 
    id="name" 
    name="fullname" 
    value={data.fullname}
    onChange={InputEvent} 
    placeholder="Enter Your Full Name" required/>
   
  </div>
  <div className=" mb-3 ">
    <label for="exampleFormControlInput1">Phone</label>
    <input type="text" 
    maxLength="10" 
    className="form-control" 
    id="mob" 
    name="phone" 
    value={data.phone} 
    onChange={InputEvent} 
    placeholder="XXXXXXXXXX" required/>
  </div>
  <div className=" mb-3 ">
    <label for="exampleFormControlInput1">Email</label>
    <input type="email" 
    className="form-control" 
    id="exampleFormControlInput1" 
    name="email" 
    value={data.email} 
    onChange={InputEvent} 
    placeholder="name@example.com" required/>
  </div>
  <div className=" mb-3 ">
    <label for="exampleFormControlTextarea1">Message</label>
    <textarea className="form-control" 
    name="msg" 
    value={data.msg} 
    onChange={InputEvent}  
    id="exampleFormControlTextarea1" 
    rows="3" required></textarea>
  </div>
  <div className="col-10">
  <button type="submit" onClick={createToDo}
  className="btn btn-outline-primary mb-2">Submit Form</button>
  </div>
</form>

            </div>
        </div>
    </div>
    </>
    );
 }

export default Contact;
